import logo from './logo.svg';
// import './App.css';


import React, { useState } from 'react';

const CellTypes = {
  NUMBER: 'NUMBER',
  TEXT: 'TEXT',
  FORMULA: 'FORMULA'
};

const Cell = ({ type, value, onChange, onBlur }) => {
  const [editing, setEditing] = useState(false);
  const [inputValue, setInputValue] = useState(value);

  const handleDoubleClick = () => {
    setEditing(true);
  };

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleBlur = () => {
    setEditing(false);
    onBlur(inputValue);
  };

  return (
    <td onDoubleClick={handleDoubleClick}>
      {editing ? (
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          autoFocus
        />
      ) : (
        <span>{value}</span>
      )}
    </td>
  );
};

const SpreadsheetRow = ({ cells, onChange, onBlur }) => {
  const handleCellChange = (index, newValue) => {
    const newCells = [...cells];
    newCells[index] = newValue;
    onChange(newCells);
  };

  return (
    <tr>
      {cells.map((cell, index) => (
        <Cell
          key={index}
          type={cell.type}
          value={cell.value}
          onChange={(newValue) => handleCellChange(index, newValue)}
          onBlur={(newValue) => onBlur(index, newValue)}
        />
      ))}
    </tr>
  );
};

const Spreadsheet = () => {
  const [rows, setRows] = useState([
    [{ type: CellTypes.NUMBER, value: '10' }, { type: CellTypes.NUMBER, value: '20' }],
    [{ type: CellTypes.TEXT, value: 'Hello' }, { type: CellTypes.FORMULA, value: '=A1+A2' }]
  ]);

  const handleCellChange = (rowIndex, cellIndex, newValue) => {
    const newRows = [...rows];
    newRows[rowIndex][cellIndex].value = newValue;
    setRows(newRows);
  };

  const handleCellBlur = (rowIndex, cellIndex, newValue) => {
    // If it's a formula cell, evaluate the formula
    if (rows[rowIndex][cellIndex].type === CellTypes.FORMULA) {
      try {
        const evalValue = eval(newValue.slice(1));
        handleCellChange(rowIndex, cellIndex, evalValue.toString());
      } catch (error) {
        console.error('Error evaluating formula:', error);
      }
    } else {
      handleCellChange(rowIndex, cellIndex, newValue);
    }
  };

  return (
    <table>
      <tbody>
        {rows.map((row, rowIndex) => (
          <SpreadsheetRow
            key={rowIndex}
            cells={row}
            onChange={(newRow) => {
              const newRows = [...rows];
              newRows[rowIndex] = newRow;
              setRows(newRows);
            }}
            onBlur={(cellIndex, newValue) => handleCellBlur(rowIndex, cellIndex, newValue)}
          />
        ))}
      </tbody>
    </table>
  );
};

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Spreadsheet />
      </header>
    </div>
  );
}

export default App;
