import logo from './logo.svg';
import './App.css';


import React, { useState } from 'react';

const ExcelCell = ({ value, onChange }) => {
  const [editing, setEditing] = useState(false);
  const [inputValue, setInputValue] = useState(value);

  const handleDoubleClick = () => {
    setEditing(true);
  };

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleBlur = () => {
    setEditing(false);
    onChange(inputValue);
  };

  return (
    <td onDoubleClick={handleDoubleClick}>
      {editing ? (
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          autoFocus
        />
      ) : (
        <span>{value}</span>
      )}
    </td>
  );
};

const ExcelTable = () => {
  const [cell1, setCell1] = useState('Cell 1');
  const [cell2, setCell2] = useState('Cell 2');

  const handleCell1Change = (newValue) => {
    setCell1(newValue);
  };

  const handleCell2Change = (newValue) => {
    setCell2(newValue);
  };

  return (
    <table>
      <tbody>
        <tr>
          <ExcelCell value={cell1} onChange={handleCell1Change} />
          <ExcelCell value={cell2} onChange={handleCell2Change} />
        </tr>
      </tbody>
    </table>
  );
};

// const App = () => {
//   return (
//     <div>
//       <h1>React Excel-like App</h1>
//       <ExcelTable />
//     </div>
//   );
// };

// export default App;

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <ExcelTable />
      </header>
    </div>
  );
}

export default App;
