const CellTypes = {
  TEXT: 'TEXT',
  NUMBER: 'NUMBER',
  FORMULA: 'FORMULA'
};

const Cell = ({ type, value, onChange }) => {
  const [editing, setEditing] = useState(false);
  const [inputValue, setInputValue] = useState(value);

  useEffect(() => {
    setInputValue(value);
  }, [value]);

  const handleDoubleClick = () => {
    setEditing(true);
  };

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleBlur = () => {
    setEditing(false);
    onChange(inputValue);
  };

  return (
    <td onDoubleClick={handleDoubleClick}>
      {editing ? (
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          autoFocus
        />
      ) : (
        <span>{value}</span>
      )}
    </td>
  );
};

const Spreadsheet = () => {
  const [cells, setCells] = useState([
    { type: CellTypes.TEXT, value: 'Grandma' },
    { type: CellTypes.NUMBER, value: '10' },
    { type: CellTypes.NUMBER, value: '20' },
    { type: CellTypes.FORMULA, value: '' }
  ]);

  const handleCellChange = (index, newValue) => {
    const newCells = [...cells];
    newCells[index].value = newValue;
    setCells(newCells);
  };

  useEffect(() => {
    const sum = cells.slice(1, 3).reduce((acc, cur) => acc + parseFloat(cur.value), 0);
    handleCellChange(3, sum.toString());
  }, [cells]);

  return (
    <table>
      <tbody>
        {cells.map((cell, index) => (
          <tr key={index}>
            <td>Cell {index}</td>
            <Cell
              type={cell.type}
              value={cell.value}
              onChange={(newValue) => handleCellChange(index, newValue)}
            />
          </tr>
        ))}
      </tbody>
    </table>
  );
};

// main app
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Spreadsheet />
      </header>
    </div>
  );
}

export default App;
