const CellTypes = {
  NUMBER: 'NUMBER',
  TEXT: 'TEXT',
  FORMULA: 'FORMULA'
};

const Cell = ({ type, value, onChange, onBlur }) => {
  const [editing, setEditing] = useState(false);
  const [inputValue, setInputValue] = useState(value);

  const handleDoubleClick = () => {
    setEditing(true);
  };

  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleBlur = () => {
    setEditing(false);
    onBlur(inputValue);
  };

  return (
    <td onDoubleClick={handleDoubleClick}>
      {editing ? (
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          autoFocus
        />
      ) : (
        <span>{value}</span>
      )}
    </td>
  );
};

const SpreadsheetColumn = ({ cells, onChange, onBlur }) => {
  const handleCellChange = (index, newValue) => {
    const newCells = [...cells];
    newCells[index] = newValue;
    onChange(newCells);
  };

  return (
    <td>
      {cells.map((cell, index) => (
        <Cell
          key={index}
          type={cell.type}
          value={cell.value}
          onChange={(newValue) => handleCellChange(index, newValue)}
          onBlur={(newValue) => onBlur(index, newValue)}
        />
      ))}
    </td>
  );
};

const Spreadsheet = () => {
  const [columns, setColumns] = useState([
    [{ type: CellTypes.NUMBER, value: '10' }, { type: CellTypes.NUMBER, value: '20' }, { type: CellTypes.FORMULA, value: '' }],
    [{ type: CellTypes.TEXT, value: 'Hello' }, { type: CellTypes.TEXT, value: 'World' }, { type: CellTypes.FORMULA, value: '' }]
  ]);

  const handleCellChange = (columnIndex, cellIndex, newValue) => {
    const newColumns = [...columns];
    newColumns[columnIndex][cellIndex].value = newValue;
    setColumns(newColumns);
  };

  const handleCellBlur = (columnIndex, cellIndex, newValue) => {
    // If it's a formula cell, evaluate the formula
    if (columns[columnIndex][cellIndex].type === CellTypes.FORMULA) {
      try {
        const sum = columns[columnIndex].slice(0, 2).reduce((acc, cur) => acc + parseFloat(cur.value), 0);
        handleCellChange(columnIndex, cellIndex, sum.toString());
      } catch (error) {
        console.error('Error evaluating formula:', error);
      }
    } else {
      handleCellChange(columnIndex, cellIndex, newValue);
    }
  };

  return (
    <table>
      <tbody>
        {columns.map((column, columnIndex) => (
          <tr key={columnIndex}>
            <SpreadsheetColumn
              cells={column}
              onChange={(newColumn) => {
                const newColumns = [...columns];
                newColumns[columnIndex] = newColumn;
                setColumns(newColumns);
              }}
              onBlur={(cellIndex, newValue) => handleCellBlur(columnIndex, cellIndex, newValue)}
            />
          </tr>
        ))}
      </tbody>
    </table>
  );
};

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Spreadsheet />
      </header>
    </div>
  );
}

export default App;
